import "./HeroimageStyles.css";
import React from 'react';
import { Link } from "react-router-dom";
import Typewriter from './Typewriter'; // Adjust the path as necessary

const Heroimage = () => {
  const professions = ["Aspiring Developer", "Web Developer", "Web Designer", "AI Expert"];
  const delay = 100; // Adjust typing delay as necessary

  return (
    <div className="hero">
      <div className="mask"></div>
      <div className="content">
        <p className="acer">Hi, I'M AAMIR</p>
        <h1>
          <Typewriter text={professions.join(' | ')} delay={delay} infinite={true} />
        </h1>
        <div>
          <Link to="/project" className="btn">Projects</Link>
          <Link to="/contact" className="btn btn-light">Contact</Link>
        </div>
      </div>
    </div>
  );
}

export default Heroimage;
