import React, { useState } from 'react';
import axios from 'axios';
import emailjs from 'emailjs-com';
import { toast } from 'react-toastify';
import './FormStyles.css';

const Form = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [errors, setErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);

  // Validation function
  const validate = () => {
    let errors = {};
    let valid = true;

    if (!formData.name) {
      errors.name = 'Name is required';
      valid = false;
    }
    if (!formData.email) {
      errors.email = 'Email is required';
      valid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email address is invalid';
      valid = false;
    }
    if (!formData.subject) {
      errors.subject = 'Subject is required';
      valid = false;
    }
    if (!formData.message) {
      errors.message = 'Message is required';
      valid = false;
    }

    setErrors(errors);
    return valid;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      try {
        // Post data to Django backend
        const response = await axios.post('http://127.0.0.1:8000/form', formData);
        console.log(response.data);

        // Prepare email parameters
        const templateParams = {
          from_name: formData.name,
          to_name: 'aamir2saiyad@gmail.com', // Replace with the recipient's email or name
          subject: formData.subject,
          message: formData.message
        };

        // Send email using EmailJS
        emailjs
          .send('your_service_id', 'your_template_id', templateParams, 'your_user_id')
          .then(
            (emailResponse) => {
              console.log('Email sent successfully', emailResponse.status, emailResponse.text);
              toast.success('Your message has been sent and stored successfully!', {
                position: toast.POSITION.BOTTOM_CENTER
              });
              setFormData({
                name: '',
                email: '',
                subject: '',
                message: ''
              });
              setErrors({});
              setShowPopup(true);
              setTimeout(() => setShowPopup(false), 3000); // Hide popup after 3 seconds
            },
            (err) => {
              console.error('Failed to send email', err);
              toast.error('Your message was stored but the email could not be sent.', {
                position: toast.POSITION.BOTTOM_CENTER
              });
            }
          );
      } catch (error) {
        console.error('There was an error submitting the form!', error);
        toast.error('There was an error storing your message.', {
          position: toast.POSITION.BOTTOM_CENTER
        });
      }
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <label>Your Name</label>
        <input 
          type="text" 
          name="name" 
          value={formData.name} 
          onChange={handleChange} 
        />
        {errors.name && <p className="error">{errors.name}</p>}

        <label>Email</label>
        <input 
          type="email" 
          name="email" 
          value={formData.email} 
          onChange={handleChange} 
        />
        {errors.email && <p className="error">{errors.email}</p>}

        <label>Subject</label>
        <input 
          type="text" 
          name="subject" 
          value={formData.subject} 
          onChange={handleChange} 
        />
        {errors.subject && <p className="error">{errors.subject}</p>}

        <label>Message</label>
        <textarea 
          rows="6" 
          placeholder="Type your message here" 
          name="message" 
          value={formData.message} 
          onChange={handleChange} 
        />
        {errors.message && <p className="error">{errors.message}</p>}

        <button className="btn" type="submit">Submit</button>
      </form>

      {showPopup && <div className="popup">Form submitted successfully!</div>}
    </div>
  );
}

export default Form;
