// export default WorkCard

import "./WorkCardStyles.css";

import React, { useState, useRef, useEffect } from 'react';

import { NavLink } from "react-router-dom";

const WorkCard = (props) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const frontRef = useRef(null);
  const backRef = useRef(null);
  const cardRef = useRef(null);

  useEffect(() => {
    const card = cardRef.current;
    const frontHeight = frontRef.current.offsetHeight;
    const backHeight = backRef.current.offsetHeight;
    card.style.height = `${isFlipped ? backHeight : frontHeight}px`;
  }, [isFlipped]);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div
    className={`project-card ${isFlipped ? 'flipped' : ''}`}
    onClick={handleFlip}
    ref={cardRef}
  >
    <div className="thefront" ref={frontRef}>
      <img src={props.imgsrc} alt="any" />
      <h2 className="project-title">{props.title}</h2>
    </div>
    <div className="theback" ref={backRef}>
      <div className="pro-details">
        <p>{props.text}</p>
        <div className="pro-btns">
          <NavLink to={props.view} className="btn">View</NavLink>
          <NavLink to="url.com" className="btn">Source Code</NavLink>
        </div>
      </div>
    </div>
  </div>
);
};

export default WorkCard;
