import React, { useState } from 'react';
import { NavLink } from "react-router-dom";
import "./WorkCardStyles.css";

const WorkCard = (props) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className={`flip-container ${isFlipped ? 'flipped' : ''}`} onClick={handleFlip}>
      <div className="flipper">
        <div className="front project-card">
          <img src={props.imgsrc} alt="any" />
          <h2 className="project-title">{props.title}</h2>
          <div className="pro-details">
            <p>{props.text}</p>
            <div className="pro-btns">
              <NavLink to={props.view} className="btn">View</NavLink>
              <NavLink to="url.com" className="btn">Source</NavLink>
            </div>
          </div>
        </div>
        <div className="back">
          <h2 className="project-title">{props.title} - Details</h2>
          <p>Additional information about the project can go here.</p>
          <button onClick={handleFlip}>Back to Front</button>
        </div>
      </div>
    </div>
  );
};

export default WorkCard;
