const AboutContent = () => {
  const [modalImage, setModalImage] = useState(null);

  const openModal = (imageSrc) => {
    setModalImage(imageSrc);
  };

  const closeModal = () => {
    setModalImage(null);
  };

  return (
    <div className="about">
      <div className="left">
        <h1>Who Am I?</h1>
        <div className="bio-text">
          <p>I am a Multidisciplinary Technologist with expertise in web development (HTML, CSS, JavaScript, React), backend development (Python, Flask), database management (PHP and SQL) and advanced technologies (Machine Learning, Artificial Intelligence).</p>
        </div>
        <div className="btn-container">
          <Link to="/contact">
            <button className="btn">Contact</button>
          </Link>
          <a href="/cv.pdf" download>
            <button className="btn">Download CV</button>
          </a>
        </div>
      </div>
      <div className="right">
        <h1>Skills</h1>
        <div className="skills">
          <div className="skill">
            <FaPython color="#3776AB" />
            <p>Python</p>
          </div>
          <div className="skill">
            <FaJsSquare color="#F7DF1E" />
            <p>JavaScript</p>
          </div>
          <div className="skill">
            <FaPhp color="#777BB4" />
            <p>PHP</p>
          </div>
          <div className="skill">
            <FaHtml5 color="#E34F26" />
            <p>HTML</p>
          </div>
          <div className="skill">
            <FaCss3Alt color="#1572B6" />
            <p>CSS</p>
          </div>
          <div className="skill">
            <FaWordpress color="#21759B" />
            <p>WordPress</p>
          </div>
          <div className="skill">
            <FaRobot color="#FFD700" />
            <p>Machine Learning</p>
          </div>
          <div className="skill">
            <FaBullhorn color="#FF4500" />
            <p>Digital Marketing</p>
          </div>  
        </div>    
      </div>
      <div className="bottom">
        <h1>Certificates</h1>
        <div className="certificate" onClick={() => openModal(digitalMarketingImage)}>
          <img className="jpeg" src={digitalMarketingImage} alt="Digital Marketing Certificate" />
          <div className="content">
            <h2>Digital Marketing</h2>
          </div>
        </div>
        <div className="certificate" onClick={() => openModal(PhpImage)}>
          <img className="jpeg" src={PhpImage} alt="PHP Certificate" />
          <div className="content">
            <h2>PHP</h2>
          </div>
        </div>
        <div className="certificate" onClick={() => openModal(wordpress)}>
          <img className="jpeg" src={wordpress} alt="WordPress Certificate" />
          <div className="content">
            <h2>WordPress</h2>
          </div>
        </div>
      </div>

      {/* Modal for displaying enlarged image */}
      {modalImage && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content">
            <img src={modalImage} alt="Enlarged Certificate" className="modal-image" />
          </div>
        </div>
      )}
    </div>
  );
};

export default AboutContent;
